      out.write("\r\n");
      out.write("    let dist = parseFloat(mileageInput);\r\n");
      out.write("    let rate = 0;\r\n");
      out.write("\r\n");
      out.write("    // ==========================================\r\n");
      out.write("    //  UPDATED RATES\r\n");
      out.write("    //  1-50km   -> 0.70\r\n");
      out.write("    //  51-200km -> 0.55\r\n");
      out.write("    //  >200km   -> 0.45\r\n");
      out.write("    // ==========================================\r\n");
      out.write("    if (isNaN(dist) || dist <= 0) {\r\n");
      out.write("        rate = 0;\r\n");
      out.write("    } else if (dist >= 1 && dist <= 50) {\r\n");
      out.write("        rate = 0.70;\r\n");
      out.write("    } else if (dist > 50 && dist <= 200) {\r\n");
      out.write("        rate = 0.55;\r\n");
      out.write("    } else if (dist > 200) {\r\n");
      out.write("        rate = 0.45;\r\n");
      out.write("    }\r\n");
      out.write("\r\n");
      out.write("    // 2. Update Rate Field (Fixed to 2 decimal places)\r\n");
      out.write("    rateField.value = rate.toFixed(2);\r\n");
      out.write("\r\n");
      out.write("    // 3. Calculate Total Amount (Rate * Mileage)\r\n");
      out.write("    if (dist > 0) {\r\n");
